mapper.select:
select orderInfo.order_id, productInfo.layer_id , (select order_version from odr_prd.order_info where order_id = orderInfo.order_id) order_version ,(select schema from odr_prd.layer_info where layer_id = productInfo.layer_id) schema_name ,(select user_id from odr_prd.order_info where order_id = orderInfo.order_id) user_id , array_agg(productInfo.bjcd) bjcds , ST_AsGeoJSON(COALESCE(ST_UNION( productInfo.wkb_geometry))) bjcd_geoms from (select * from odr_prd.order_info oInfo where 1=1
and oInfo.user_id = #{userId}
and oInfo.update_status = '1' ) orderInfo ,( select pInfo.order_id , pInfo.layer_id ,COALESCE(COALESCE(sido.bjcd, sgg.bjcd), umd.bjcd) bjcd ,ST_Transform(COALESCE(COALESCE(sido.wkb_geometry, sgg.wkb_geometry), umd.wkb_geometry), 5179) wkb_geometry from odr_prd.product_info pInfo left OUTER JOIN nfsd.nf_a_g01102 sido on pInfo.bjcd = sido.bjcd left OUTER JOIN nfsd.nf_a_g01103 sgg on pInfo.bjcd = sgg.bjcd left OUTER JOIN nfsd.nf_a_g01106 umd on pInfo.bjcd = umd.bjcd where (select order_type from odr_prd.order_info where order_id = pInfo.order_id) = '0' union select pInfo.order_id, pInfo.layer_id ,dycd bjcd ,ST_Transform(doyeup.wkb_geometry, 5179) wkb_geometry from odr_prd.product_info pInfo, cdm_2015.n3a_h0010000 doyeup where pInfo.map_num = doyeup.dycd and (select order_type from odr_prd.order_info where order_id = pInfo.order_id) = '1' ) productInfo where 1=1 and orderInfo.order_id = productInfo.order_id group by orderInfo.order_id, productInfo.layer_id

mapper.update:
update odr_prd.order_info set
 	update_status = '3'
 where 1=1
   and order_id = #{orderId}
   and user_id = #{userId}
   and update_status = '1'

mapper.select:
select * from plugin.sdmc_pg_user
 where email = #{0}
   and password = #{1}
